import React from 'react';
import { loadStripe } from '@stripe/stripe-js';

const stripePromise = loadStripe(
  'pk_live_51NSOIZAlMkcjF6tXEcS4BDh0gSuNSoJLmAqVTy4U6TzelGUBiSYH312cSb2dEq0HvassAt8sojfCazeJuaVz9mZm00iPLYZjtN'
);

const PaymentForm = () => {
  const handleClick = async () => {
    const stripe = await stripePromise;

    const { error } = await stripe!.redirectToCheckout({
      items: [{ sku: 'sku_123', quantity: 1 }], // Replace with your product details
      successUrl: 'https://your-website.com/success', // Redirect URL after successful payment
      cancelUrl: 'https://your-website.com/cancel', // Redirect URL if payment is canceled
    });

    if (error) {
      console.error(error);
    }
  };
};

export default PaymentForm;
