import React, { FC } from 'react';
import b4Front from '../../assets/project-1/front-before.jpg';
import afterFront from '../../assets/project-1/front-mid.jpg';

interface PictureProps {
  src: string;
  caption: string;
}
interface ProjectProps {
  title: string;
  hours: number;
  pictures: PictureProps[];
}
const Projects: FC = () => {
  const projects: [ProjectProps] = [
    {
      title: 'Deep weeding',
      hours: 50,
      pictures: [
        { src: b4Front, caption: 'Before' },
        { src: afterFront, caption: 'After' },
      ],
    },
  ];

  return (
    <>
      <h1>Projects</h1>
      {projects.map((project, i) => (
        <div key={i}>
          <h2>
            {project.title} - {project.hours} hours
          </h2>
          {project.pictures.map((picture, i) => (
            <div key={i}>
              <img
                src={picture.src}
                alt={picture.caption}
                className='sizeToFit'
              />
              <p>{picture.caption}</p>
            </div>
          ))}
        </div>
      ))}
    </>
  );
};

export default Projects;
