import React, { useState, FC } from 'react';
import b4Front from '../../assets/project-1/front-before.jpg';
import afterFront from '../../assets/project-1/front-mid.jpg';
import tala1 from '../../assets/Tala/back-rocks-weeds-before.jpg';
import tala2 from '../../assets/Tala/back-rocks-weeds-after.jpg';
import tala3 from '../../assets/Tala/front-from-door-before.jpg';
import tala4 from '../../assets/Tala/front-from-door-after.jpg';
import tala5 from '../../assets/Tala/back-side-shed-before.jpg';
import tala6 from '../../assets/Tala/back-side-shed-after.jpg';

interface BeforeAfterProps {
  before: PictureProps;
  after: PictureProps;
}
interface PictureProps {
  src: string;
  caption: string;
}
interface ProjectProps {
  title: string;
  hours: number;
  photos: BeforeAfterProps[];
}
const Projects: FC = () => {
  const [showBefore, setShowBefore] = useState(true);

  const projects: ProjectProps[] = [
    {
      title: 'Deep weeding',
      hours: 50,
      photos: [
        {
          before: { src: b4Front, caption: 'Before' },
          after: { src: afterFront, caption: 'After' },
        },
      ],
    },
    {
      title: 'Weed, Rocks, Chainsaw',
      hours: 30,
      photos: [
        {
          before: {
            src: tala1,
            caption:
              'Asked to remove weeds from gravel, raised beds, &  trim plants',
          },
          after: {
            src: tala2,
            caption:
              'Removed weeds from gravel, cleared raised beds, defined plants & gravel area',
          },
        },
        {
          before: { src: tala3, caption: 'Before' },
          after: { src: tala4, caption: 'After' },
        },
        {
          before: { src: tala5, caption: 'Before' },
          after: { src: tala6, caption: 'After' },
        },
      ],
    },
  ];

  return (
    <div className='projects'>
      <h1>Projects</h1>
      {projects.map((project, i) => (
        <div key={i}>
          <h2>
            {project.title} - {project.hours} hours
          </h2>
          {project.photos.map((photo, i) => (
            <div key={i}>
              <div
                className={`image-container ${showBefore ? 'before' : 'after'}`}
                onClick={() => setShowBefore(!showBefore)}
              >
                <img
                  src={showBefore ? photo.before.src : photo.after.src}
                  alt={showBefore ? photo.before.caption : photo.after.caption}
                />
              </div>

              <p>
                {showBefore
                  ? 'Before' + photo.before.caption
                  : photo.after.caption}
              </p>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

export default Projects;
